mapper.select:
SELECT * FROM tb_staff AS s JOIN tb_member AS m ON s.member_id = m.member_id LEFT JOIN tb_commute AS c ON c.staff_code = s.staff_code AND c.commute_date = CURDATE()
s.staff_code= #{send_code}
ORDER BY s.staff_in_date DESC;

mapper.update:
UPDATE tb_staff
SET
	staff_name=#{staffName},
	staff_part=#{staffPart},
	staff_rank=#{staffRank}
WHERE staff_code=#{staffCode}

mapper.insert:

	SELECT
		(case COUNT(*)
		when 0 then'staff_code_1'
		else CONCAT('staff_code_',MAX(CAST(SUBSTRING(staff_code,12) AS DECIMAL))+1) 
		END)AS staff_code
	FROM
		tb_staff

INSERT INTO tb_staff
	(
		staff_code
		, member_id
		, staff_name
		, staff_part
		, staff_rank
		, staff_in_date
		, staff_out_date
	)
VALUES (
		  #{staffCode}
		, #{member.memberId}
		, #{staffName}
		, #{staffPart}
		, #{staffRank}
		, CURDATE()
		, null
		)

mapper.update:
UPDATE tb_commute
SET
	commute_end_time=NOW(),
	commute_working_hour=#{commuteWorkingHour}
WHERE commute_code=#{commuteCode}
